INSERT INTO Categoria (Nombre, Descripcion, Popularidad) VALUES ('RPG', 'Sumérgete en mundos de fantasía, toma decisiones clave y lucha contra monstruos en este género de juego que te permite vivir aventuras épicas.', 8);
INSERT INTO Categoria (Nombre, Descripcion, Popularidad) VALUES ('Action-Adventure', 'Explora entornos vibrantes, resuelve acertijos y participa en emocionantes secuencias de acción en este género de juego que combina elementos de acción y aventura.', 7);
INSERT INTO Categoria (Nombre, Descripcion, Popularidad) VALUES ('Battle Royale', 'Enfréntate a otros jugadores en una lucha a vida o muerte en un mapa que se hace cada vez más pequeño en este género de juego de supervivencia intensa.', 9);
INSERT INTO Categoria (Nombre, Descripcion, Popularidad) VALUES ('Sports', 'Disfruta de competiciones deportivas realistas y emocionantes en una amplia variedad de disciplinas, desde fútbol hasta baloncesto y carreras de autos.', 10);
INSERT INTO Categoria (Nombre, Descripcion, Popularidad) VALUES ('Tactical Shooter', 'Demuestra tus habilidades estratégicas y tu puntería precisa en este género de juego que se centra en el combate táctico y la coordinación del equipo.', 7);
INSERT INTO Categoria (Nombre, Descripcion, Popularidad) VALUES ('Party', 'Reúne a tus amigos para una diversión instantánea con juegos que son fáciles de aprender y adecuados para multijugador, local y perfectos para cualquier fiesta.', 8);
